$(function() {
  $("#lorries").animate(
    { backgroundPosition: "10000px 0px" },
    240000,
    "linear"
  );
  $("#background-vehicles").animate(
    { backgroundPosition: "-8000px 0px" },
    140000,
    "linear"
  );
  $("#background-small-cranes").animate(
    { backgroundPosition: "-10000px 0px" },
    500000,
    "linear"
  );
  $("#clouds").animate(
    { backgroundPosition: "-10000px -0px" },
    960000,
    "linear"
  );
});
$(function() {
  var a = new Date();
  a = new Date(a.getFullYear() + 0, 12 - 1, 31);
  $("#defaultCountdown").countdown({ until: a });
  $("#year").text(a.getFullYear());
});
